modulo = (10 ** 9) + 7

def pre(cnt, limit):
    f = [0]
    for idx in range(cnt):
        f.append(1 << idx)
    for idx in range(cnt + 1, limit):
        val = 0
        for p in range(1, cnt + 1):
            val += f[idx - p]
        val %= modulo
        f.append(val)
    return f

# class Solution:
#     def countTexts(self, pressedKeys: str) -> int:
def countTexts(pressedKeys: str) -> int:
        key_map = dict((str(v), 3) for v in range(2, 10))
        key_map['7'] = 4
        key_map['9'] = 4

        limit = min(10 ** 5 + 3, len(pressedKeys) + 3)
        # print(pre(3, limit)[:40])
        # print(pre(4, limit)[:40])
        pres = dict()
        pres[3] = pre(3, limit)
        pres[4] = pre(4, limit)

        rptr, lptr = 0, 0
        # seq = []
        ans = 1
        while rptr < len(pressedKeys):
            while rptr < len(pressedKeys) and pressedKeys[rptr] == pressedKeys[lptr]:
                rptr += 1
            ch = pressedKeys[lptr]
            # seq.append(rptr - lptr)
            ans *= pres[key_map[ch]][rptr - lptr]
            ans %= modulo
            lptr = rptr

        if lptr < rptr:
            ch = pressedKeys[lptr]
            # seq.append(rptr - lptr)
            ans *= pres[key_map[ch]][rptr - lptr]
            ans %= modulo
            lptr = rptr

        # print(seq)
        return ans

print(countTexts('2'))
print(countTexts('7'))
print(countTexts('22'))
print(countTexts('77'))
print(countTexts('222'))
print(countTexts('777'))
print(countTexts('2222'))
print(countTexts('7777'))
print(countTexts('22233'))
print(countTexts('222222222222222222222222222222222222'))
print(countTexts(""))